LL M[511][511];
LL P[511][511];

LL MINUS(int l, int r) {
    LL tmp = M[r][r] - M[r][l] - M[l][r] + M[l][l];
    LL ret = 0;
    for (int i=l; i<r; i++) for (int j=i+1; j<r; j++) {
        ret += A[i][j];
    }
    assert(tmp == ret);
    return tmp;
}
LL PLUS(int k, int l, int r) {
    LL tmp = P[r][k] - P[l][k];
    LL ret = 0;
    for (int i=l; i<r; i++) for (int j=0; j<k; j++) {
	ret += A[i][j];
    }
    assert(tmp == ret);
    return tmp;
}

