			chmin(dp[i + 1][i + 1][j], dp[i][j][k] + add);
		}

		// do nothing 
		{
			ll add = 0LL;
			add += mns[i + 1][i];
			if(vj - 1 >= 0) add -= mns[i + 1][vj - 1];
			if(vk - 1 >= 0) add += pls[i + 1][vk - 1];
			chmin(dp[i + 1][j][k], dp[i][j][k] + add);
		}
	}

	ll ans = inf;
	rep(j, n + 1) rep(k, n + 1) chmin(ans, dp[n - 1][j][k]);
	cout << ans << endl;

	return 0;
}

