const ll maxn = 5e2 + 100;
const ll inf = 1e15;

ll n, m, k, t;

ll dp[maxn][maxn][2];

ll A[maxn][maxn];

ll getdp(ll l, ll r, ll i) {
    if (dp[l][r][i] != -1) {
        return dp[l][r][i];
    }
    if (i == 0) {
        ll dd = 0;
        ll q;
        for (q = l; q <= r; q++) {
            dd += A[q][r];
        }
        dp[l][r][i] = dp[l][r - 1][i] + dd;
    }
    else {
        ll q;
        ll dd = 0;
        for (q = r + 1; q < n; q++) {
            dd += A[q][l - 1];
        }
        dp[l][r][i] = dp[l - 1][r][i] + dd;
    }
    return dp[l][r][i];
}

ll D[maxn][maxn];

ll get_in(ll l, ll r) {
    while (r >= n) {
        r--;
    }
    while (l >= n) {
        l--;
    }
    if (l > r) {
        return 0;
    }
    ll ans = getdp(l, r, 0); 
    // cout << "IN " << l << " " << r  << " " << ans << endl;
    return ans;
}

ll get_out(ll l, ll r) {
    while (r >= n) {
        r--;
    }
    while (l >= n) {
        l--;
    }
    while (l > r) {
        r++;
    }
    ll ans = getdp(l, r, 1);
    // cout << "OUT " << l << " " << r  << " " << ans << endl;
    return ans;
}

