        for (w = 0; w < n; w++) {
            if (w == q) {
                continue;
            }
            cin >> A[q][w];
        }
    }
    for (q = 0; q < maxn; q++) {
        for (w = 0; w < maxn; w++) {
            for (e = 0; e < 2; e++) {
                dp[q][w][e] = -1;
            }
        }
    }
    for (q = 0; q < maxn; q++) {
        dp[q][q][0] = 0;
    }

    for (q = 0; q < maxn; q++) {
        dp[0][q][1] = 0;
    }
    for (q = 0; q < maxn; q++) {
        dp[q][n - 1][1] = 0;
    }

    // ll ans = 0;
    for (q = 0; q < maxn; q++) {
        for (w = 0; w < maxn; w++) {
            D[q][w] = inf;
        }
    }
    D[0][0] = 0;
    for (q = 1; q <= n + 1; q++) {
        for (w = 0; w < q; w++) {
            D[q][w] = inf;
            for (e = 0; e <= w; e++) {
                ll dans = D[w][e];
                ll d = get_in(w, q - 1);
                ll d1 = get_out(w, q - 1) - get_out(e, q - 1);
                dans += d + d1;
                D[q][w] = min(D[q][w], dans);
            }
            // cout << q << " " << w << " " << "D " << D[q][w] << endl << endl;
        }
    }
    ll ans = inf;
    for (q = 0; q <= n; q++) {
        ans = min(ans, D[n + 1][q]);
    }
    cout << ans;
    return 0;
}

