const int MAXN = 500;

int n;

int A[MAXN + 5][MAXN + 5], B[MAXN + 5][MAXN + 5];

inline void input()
{
	n = read<int>();
	for(int i = 1; i <= n; ++i)
		for(int j = 1; j <= n; ++j)
		{
			if(i < j) A[i][j] = read<int>();
			if(i > j) B[j][i] = read<int>();
		}
}

inline void solve()
{
	static LL S0[MAXN + 5][MAXN + 5], pre[MAXN + 5][MAXN + 5], S[MAXN + 5][MAXN + 5][MAXN + 5];

	for(int len = 1; len <= n; ++len)
		for(int i = 1; i + len <= n; ++i)
		{
			int j = i + len;
			S0[i][j] = S0[i + 1][j] + S0[i][j - 1] - S0[i + 1][j - 1] + B[i][j];
		}

	for(int i = 1; i <= n; ++i)
	{
		pre[i][0] = 0;
		for(int j = 1; j <= n; ++j) pre[i][j] = pre[i][j - 1] + B[i][j];
	}

	for(int j = 1; j <= n; ++j) for(int k = j + 1; k <= n; ++k)
		for(int i = j; i >= 0; --i) S[i][j][k] = S[i + 1][j][k] + pre[i][k] - pre[i][j];

	static LL suf[MAXN + 5][MAXN + 5], S1[MAXN + 5][MAXN + 5];

	for(int i = 1; i <= n; ++i)
	{
		suf[0][i] = 0;
		for(int j = 1; j <= i; ++j) suf[j][i] = suf[j - 1][i] + A[j][i];
	}

	for(int i = 1; i <= n; ++i)
		for(int j = i + 1; j <= n; ++j)
			S1[i][j] = S1[i][j - 1] + suf[i][j];

	static LL f[MAXN + 5][MAXN + 5];

	for(int i = 1; i <= n; ++i) f[0][i] = S0[1][i];
	for(int k = 1; k <= n; ++k) for(int j = 1; j < k; ++j)
	{
		LL res = S0[j + 1][k] + S1[j][k];
		for(int i = 0; i < j; ++i)
			chkmax(f[j][k], res + f[i][j] + S[i + 1][j][k]);
	}

	LL sum = 0;
	for(int i = 1; i <= n; ++i) for(int j = 1; j <= n; ++j)
		sum += A[i][j] + B[i][j];

	LL ans = 0;
	for(int i = 0; i < n; ++i) chkmax(ans, f[i][n]);
	printf("%lld\n", sum - ans);
}

