void solve() {
	cin >> N;
	rep(i, 0, N) {
		rep(j, 0, N) {
			if(i == j) continue;
			cin >> A[i][j];
		}
	}
	rep(i, 0, N) {
		rep(j, 0, i) {
			U[i][j + 1] = U[i][j] + A[j][i];
		}
	}
	rep(i, 0, N) {
		rep(j, 0, i) {
			D[i][j + 1] = D[i][j] + A[i][j];
		}
	}
	rep(i, 0, N + 1) {
		rep(j, 0, N + 1) {
			rep(k, 0, N + 1) {
				dp[i][j][k] = linf;
			}
		}
	}
	dp[1][0][0] = 0;
	rep(i, 1, N) {
		rer(j, N, 0) {
			rer(k, N, 0) {
				if(dp[i][j][k] == linf) continue;
				MIN(dp[i][k][i], dp[i][j][k]);
			}
		}
		rep(j, 0, N) {
			rep(k, 0, N) {
				// debug(i, j, k, dp[i][j][k], U[i][i] - U[i][k], D[i][j]);
				MIN(dp[i + 1][j][k], dp[i][j][k] + (U[i][i] - U[i][k]) + D[i][j]);
			}
		}
	}
	ll res = linf;
	rep(j, 0, N) {
		rep(k, 0, N) {
			// debug(N, j, k, dp[N][j][k]);
			MIN(res, dp[N][j][k]);
		}
	}
	cout << res << "\n";
}

