    vvll pref(N, vll(N + 1));
    F0R (i, N) F0R (j, N) pref[i][j+1] = pref[i][j] + a[i][j];

    vvll ferp(N+1, vll(N));
    F0R (i, N) F0R (j, N) ferp[i+1][j] = ferp[i][j] + a[i][j];

    map<pii, ll> dp; dp[{ 0, 1 }] = 0;

    FOR (i, 1, N) {
        decltype(dp) DP;
        trav (p, dp) {
            int pc, cc; tie(pc, cc) = p.f; ll cost = p.s;
            {
                ll stay = cost;
                // FOR (v, i - cc, i) stay += a[v][i];
                stay += ferp[i][i] - ferp[i-cc][i];
                // F0R (v, i - cc - pc) stay += a[i][v];
                stay += pref[i][i - cc - pc];

                pii key = mp(pc, cc+1);
                ll put = min(stay, DP.count(key) ? DP[key] : LLONG_MAX);
                DP[key] = put;
            }
            {
                ll go = cost + pref[i][i - cc];
                //F0R (v, i - cc) go += a[i][v];
                pii key = mp(cc, 1);
                ll put = min(go, DP.count(key) ? DP[key] : LLONG_MAX);
                DP[key] = put;
            }
        }

        dp = move(DP);
    }

    ll ans = LLONG_MAX;
    trav (p, dp) ckmin(ans, p.s);
    ps(ans);

    return 0;
}

