    		sum[0][i][j] += sum[0][i + 1][j] + sum[0][i][j - 1] - sum[0][i + 1][j - 1];
    		sum[1][i][j] += sum[1][i + 1][j] + sum[1][i][j - 1] - sum[1][i + 1][j - 1];
    	}
    }
    for (int i = 0; i <= n + 1; ++i) {
    	for (int j = 0; j <= n + 1; ++j) {
    		dp[i][j] = 1e18;
    	}
    }
    dp[0][0] = 0;
    for (int i = 0; i <= n; ++i) {
    	for (int j = i; j <= n; ++j) {
    		if (dp[i][j] == 1e18) continue;
    		for (int R = j + 1; R <= n + 1; ++R) {
    			long long d = sum[1][i + 1][n + 1] 
    						- sum[1][i + 1][R - 1] 
    						- sum[1][j + 1][n + 1] 
    						+ sum[1][j + 1][R - 1];
    			// cout << "? " << d << ' ' << i << ' ' << j << ' ' << R << endl;
    			d += sum[0][j + 1][R - 1];
    			// cout << "  " << d << ' ' << dp[i][j] + d << endl;
    			chkmin(dp[j][R], dp[i][j] + d);
    		}
       	}
    }
    long long ans = 1e18;
    for (int i = 0; i <= n; ++i) {
    	chkmin(ans, dp[i][n + 1]);
    }
    cout << ans << '\n';
}

