    int n;  cin >> n;

    for(int i = 0 ; i < n ; ++i)
    for(int j = 0 ; j < n ; ++j)    {
        int x = 0;
        if (i ^ j)
            cin >> x;

        s_r[i][j] = x + (j ? s_r[i][j - 1] : 0);
        s_c[i][j] = x + (i ? s_c[i - 1][j] : 0);
    }

    --n;

    ll  ans = 0;

    for(int i = 0 ; i <= n ; ++i)
        ans += s_c[i][i];

    for(int i = 0 ; i < n ; ++i)    {
        ll  val = 0;

        for(int j = 1 ; j <= i ; ++j)   val += s_c[j][j];
        for(int j = i ; j <= n ; ++j)   val += s_c[j][j] - s_c[i][j];

        if (ans > val)
            ans = val;
    }

    for(int i = 0 ; i < n ; ++i)
    for(int j = 0 ; j < i ; ++j)    {
        ll  offset = s_r[i + 1][j];

        for(int k = i ; k > j ; --k)
            offset += s_c[k][k] - s_c[j][k];

        f[i][j] = offset;

        for(int k = 1 ; k <= j ; ++k)   f[i][j] += s_c[k][k];
        for(int k = i ; k >  j ; --k)   offset  += s_r[k][j];

        for(int k = j ; k > 0 ; --k)    {
            offset -= s_c[i][k];
            offset += s_c[j][k];

            if (f[i][j] > f[i][k - 1] + offset)
                f[i][j] = f[i][k - 1] + offset;
        }
        ll  curval = f[i][j] - s_r[i + 1][j];

        for(int k = n ; k > i ; --k)    {
            curval += s_c[k][k] - s_c[i][k];
            curval += s_r[k][j];
        }

        if (ans > curval)
            ans = curval;
    }
    cout << ans << endl;
}

