#include <bits/stdc++.h>
using namespace std;

typedef long long ll;

const int MAXN = 505;
const ll INF = 1e18;

int N;
ll a[MAXN][MAXN];
ll dp[MAXN][MAXN];

void load() {
	scanf("%d", &N);
	for (int i = 1; i <= N; i++)
		for (int j = 1; j <= N; j++)
			if (j != i)
				scanf("%lld", a[i] + j);
}

ll rectangle(int x1, int x2, int y1, int y2) {
	return a[x2][y2] + a[x1 - 1][y1 - 1] - a[x1 - 1][y2] - a[x2][y1 - 1];
}

ll rec(int l, int r) {
	ll &ref = dp[l][r];
	if (ref != INF)
		return ref;
	if (l == 1)
		return ref = rectangle(l, r, l, r);
	for (int i = 1; i < l; i++)
		ref = min(ref, rec(i, l - 1) + rectangle(l, r, i, r));
	return ref;
}

ll solve() {
	for (int i = 0; i <= N; i++)
		for (int j = 0; j <= N; j++)
			dp[i][j] = INF;

	ll sum = 0;
	for (int i = 1; i <= N; i++)
		for (int j = 1; j < i; j++) {
			sum += a[i][j];
			a[i][j] = -a[i][j];
		}

	for (int i = 1; i <= N; i++)
		for (int j = 1; j <= N; j++)
			a[i][j] += a[i][j - 1] + a[i - 1][j] - a[i - 1][j - 1];

	ll best = INF;
	for (int i = 1; i <= N; i++)
		best = min(best, rec(i, N));

	return sum + best;
}

int main() {
	load();
	printf("%lld\n", solve());
	return 0;
}

