void pre()
{
	for (int l=1;l<n;l++)
		for (int i=1;i+l<=n;i++)
		{
			int j=i+l;
			s1[i][j]=s1[i][j-1]+s1[i+1][j]-s1[i+1][j-1]+a[i][j];
		}
	for (int i=1;i<=n;i++)
		for (int j=n;j>i;j--)
			s2[i][j]=s2[i][j+1]+a[j][i];
	for (int j=1;j<=n;j++)
		for (int i=1;i<j;i++)
			s2[i][j]+=s2[i-1][j];
}

LL dp()
{
	for (int i=0;i<=n;i++)
		for (int j=0;j<=n;j++)
			f[i][j]=inf;
	LL ans=s1[1][n];
	for (int i=1;i<=n;i++) ans=std::min(ans,s1[1][i]+s1[i+1][n]);
	for (int i=1;i<n;i++)
		for (int j=i+1;j<=n;j++)
			f[i][j]=s1[1][i]+s1[i+1][j]+s2[i][j+1];
