			S1[i][j] = S1[i][j-1];
			for(int k=i;k<j;k++) {
				S1[i][j] += A[k][j];
			}
		}
	}
	for(int i=1;i<=N;i++) {
		for(int j=1;j<i;j++) {
			S2[i][j] = S2[i][j-1] + A[i][j];
		}
	}
	rep(i, 510) rep(j, 510) dp[i][j] = 1e18;
	for(int i=1;i<=N;i++) {
