    Dinic solver(T+1, S, T);

    for(int i=0; i<N-1; i++) for(int d=-N; d<=N; d++) solver.add_edge(code(i, d), code(i+1, d), INF);
    for(int i=0; i<N; i++) for(int j=0; j<N; j++) if(i != j) for(int d=-N; d<=N; d++){
        if(i < j && d-1 >= -N) solver.add_edge(code(i, d), code(j, d-1), A[i][j]);
        if(i > j && d+1 <=  N) solver.add_edge(code(i, d), code(j, d+1), A[i][j]);
    }

    solver.add_edge(S, code(N-1, 0), INF);
    for(int d=-N; d<0; d++) solver.add_edge(code(N-1, d), T, INF);
    cout << solver.max_flow() << endl;
