		for(int j=0;j<=i;++j){
			for(int k=i+1;k<=n;++k){
				dp[k][i]=min(dp[k][i],dp[i][j]+calc(0,i+1,i+1,k,k)+calc(1,i+1,1,k,j));
			}
		}
	}
	for(int i=0;i<n;++i){
		ans=min(ans,dp[n][i]);
	}
	printf("%lld\n",ans);
	return 0;
}

