constexpr long long INF = 1'000'000'000'000'000'000;
int n;
std::vector<std::vector<int>> a;
std::vector<std::vector<long long>> dp, sum, rsum;
long long query(std::vector<std::vector<long long>> &v, int l, int r, int d, int u) {
    return v[r][u] - v[r][d] - v[l][u] + v[l][d];
}
int main() {
    std::ios::sync_with_stdio(false);
    std::cin.tie(nullptr);
    std::cin >> n;
    a.assign(n, std::vector<int>(n));
    dp.assign(n, std::vector<long long>(n, INF));
    sum.assign(n + 1, std::vector<long long>(n + 1));
    rsum.assign(n + 1, std::vector<long long>(n + 1));
    for (int i = 0; i < n; ++i)
        for (int j = 0; j < n; ++j)
            if (i != j)
                std::cin >> a[i][j];
    for (int i = 0; i < n; ++i)
        for (int j = 0; j < n; ++j)
            sum[i + 1][j + 1] = sum[i + 1][j] + sum[i][j + 1] - sum[i][j] + a[i][j];
    for (int i = 0; i < n; ++i)
        for (int j = 0; j < n; ++j)
            rsum[i + 1][j + 1] = rsum[i + 1][j] + rsum[i][j + 1] - rsum[i][j] + (i < j ? a[i][j] : 0);
    long long ans = INF;
    dp[0][0] = 0;
    for (int i = 0; i < n; ++i) {
        for (int j = i; j < n; ++j) {
            ans = std::min(ans, dp[i][j] + query(rsum, j, n, j, n));
            for (int k = j + 1; k < n; ++k)
                dp[j][k] = std::min(dp[j][k], dp[i][j] + query(rsum, j, k, j, k) + query(sum, k, n, i, j));
        }
    }
    std::cout << ans << "\n";
    return 0;
}

