        res += Sneg[i][i] - Sneg[i][k] - Sneg[k][i] + Sneg[k][k];
        res += Spos[N][i] - Spos[N][k] - Spos[j][i] + Spos[j][k];
        chmin(dp[k][i], res);
    }

    int64_t ans = INF;
    for(int j=0; j<=N; j++) chmin(ans, dp[0][j]);
    cout << ans << endl;
    return 0;
}

