const int Mod=998244353;
int t=0,Link[100010],lin[100010],f[100010],f1[100010],n,m,tt=0;
bool vis[100010];
long long dis[100010],dis1[100010];
struct edge {int nex,v,w;}e[200010],E[200010];
struct dsa{int x,y,z;}a[100010];
void Insert(int xx,int yy,int zz) {e[++t].nex=Link[xx];e[t].v=yy;e[t].w=zz;Link[xx]=t;}
void Insert_(int xx,int yy,int zz) {E[++tt].nex=lin[xx];E[tt].v=yy;E[tt].w=zz;lin[xx]=tt;} 
void Dijkstra()
{
	priority_queue < pair <long long,int> > q;
	memset(dis,10,sizeof(dis));
	q.push(make_pair(0,1));
	dis[1]=0;f[1]=1;
	while (q.size()) {
		int x=q.top().second;
		q.pop();
		if (vis[x]) continue;
		vis[x]=true;
		for (int i=Link[x];i;i=e[i].nex) 
		  if (dis[x]+e[i].w<dis[e[i].v]) {
		  	  dis[e[i].v]=dis[x]+e[i].w;
		  	  f[e[i].v]=f[x];
		      q.push(make_pair(-dis[e[i].v],e[i].v));
		  }
		  else if (dis[x]+e[i].w==dis[e[i].v]) 
		  	  f[e[i].v]+=f[x],f[e[i].v]%=Mod;
	}
}
void Dijkstra_()
{
	priority_queue <pair <long long,int> > q;
	memset(dis1,10,sizeof(dis1));
	memset(vis,0,sizeof(vis));
	q.push(make_pair(0,2));
	dis1[2]=0;f1[2]=1;
	while (q.size()) {
		int x=q.top().second;
		q.pop();
		if (vis[x]) continue;
		vis[x]=true;
		for (int i=lin[x];i;i=E[i].nex) 
		  if (dis1[x]+E[i].w<dis1[E[i].v]) {
		  	  dis1[E[i].v]=dis1[x]+E[i].w;
		  	  f1[E[i].v]=f1[x];
		      q.push(make_pair(-dis1[E[i].v],E[i].v));
		  }
		  else if (dis1[x]+E[i].w==dis1[E[i].v])
		    f1[E[i].v]+=f1[x],f1[E[i].v]%=Mod;
	}
}
int main()
{
	scanf("%d%d",&n,&m);
	for (int i=1;i<=m;i++) {
		scanf("%d%d%d",&a[i].x,&a[i].y,&a[i].z);
		Insert(a[i].x,a[i].y,a[i].z);
		Insert_(a[i].y,a[i].x,a[i].z);
	}
	Dijkstra();
	Dijkstra_();
	long long len=dis[2];

	for (int i=1;i<=m;i++) 
		if (dis[a[i].x]+dis1[a[i].y]+a[i].z==len&&1LL*f[a[i].x]*f1[a[i].y]%Mod==f[2]) {
			if (dis[a[i].y]+dis1[a[i].x]+a[i].z>len) puts("SAD");
