const ll inf = 1LL << 50;
ll dp[510][510][510];
ll A[510][510];

ll pls[510][510];
ll mns[510][510];

int main(void){
	int n;
	cin >> n;

	rep(i, n)rep(j, n){
		if(i == j) continue;
		cin >> A[i][j];
	}

	rep(i, n){
		rep(j, i){
			if(j - 1 >= 0) pls[i][j] += pls[i][j - 1];
			pls[i][j] += A[i][j];
		}
	}

	rep(i, n){
		rep(j, i){
			if(j - 1 >= 0) mns[i][j] += mns[i][j - 1];
			mns[i][j] += A[j][i];
		}
	}


	const int unused = n;

	rep(i, n + 1) rep(j, n + 1) rep(k, n + 1) dp[i][j][k] = inf; 
	dp[0][0][unused] = 0;

	rep(i, n - 1) rep(j, n + 1) rep(k, n + 1){
		if (dp[i][j][k] == inf) continue;

		const int vj = j;
		const int vk = (k != unused) ? k : -1;

		// potential increase
		{
			ll add = 0LL;
			if(vj - 1 >= 0) add += pls[i + 1][vj - 1];
			chmin(dp[i + 1][i + 1][j], dp[i][j][k] + add);
		}

		// do nothing 
		{
			ll add = 0LL;
			add += mns[i + 1][i];
			if(vj - 1 >= 0) add -= mns[i + 1][vj - 1];
			if(vk - 1 >= 0) add += pls[i + 1][vk - 1];
			chmin(dp[i + 1][j][k], dp[i][j][k] + add);
		}
	}

	ll ans = inf;
	rep(j, n + 1) rep(k, n + 1) chmin(ans, dp[n - 1][j][k]);
	cout << ans << endl;

	return 0;
}

