ll get(int tp, int a, int b, int c, int d) { //sum a <= x <= c, b <= y <= d
	return s[tp][c+1][d+1] - s[tp][c+1][b] - s[tp][a][d+1] + s[tp][a][b];
}

int main() {
	int N; cin >> N;
	rep(i, N) {
		rep(j, N) {
			if (i != j) cin >> A[i][j];
			int tp = (i > j);
			rep(t, 2) {
				s[t][i+1][j+1] = s[t][i+1][j] + s[t][i][j+1] - s[t][i][j];
				if (t == tp) {
					s[t][i+1][j+1] += A[i][j];
				}
			}
		}
	}

	rep(i, maxn) rep(j, maxn) dp[i][j] = INF;

	for (int i = 1; i < N; ++i) {
		dp[i][0] = get(0, 0, 0, i-1, i-1);
	}

