  cin.tie(0);
  int n;
  cin >> n;
  vector<vector<int>> a(n, vector<int>(n));
  long long total = 0;
  for (int i = 0; i < n; i++) {
    for (int j = 0; j < n; j++) {
      if (i != j) {
        cin >> a[i][j];
        total += a[i][j];
      }
    }
  }
  vector<vector<long long>> sumI(n + 1, vector<long long>(n + 1));
  for (int j = 0; j < n; j++) {
    sumI[0][j] = 0;
    for (int i = 0; i < n; i++) {
      sumI[i + 1][j] = sumI[i][j] + a[i][j];
    }
  }
  vector<vector<long long>> sumJ(n + 1, vector<long long>(n + 1));
  for (int i = 0; i < n; i++) {
    sumJ[i][0] = 0;
    for (int j = 0; j < n; j++) {
      sumJ[i][j + 1] = sumJ[i][j] + a[i][j];
    }
  }
  vector<vector<long long>> dp(n + 1, vector<long long>(n + 1, -1));
  dp[0][0] = 0;
  for (int i = 0; i < n; i++) {
    for (int j = i; j <= n; j++) {
      if (dp[i][j] == -1) {
        continue;
      }
      long long ft = dp[i][j];
      for (int k = j; k < n; k++) {
        ft += sumI[j][k];
        ft += sumJ[k][k] - sumJ[k][i];
        dp[j][k + 1] = max(dp[j][k + 1], ft);
      }
    }
  }
  long long ans = -1;
  for (int i = 0; i < n; i++) {
    ans = max(ans, dp[i][n]);
  }
  cout << total - ans << '\n';
  return 0;
}

