			s2[i][j]=s2[i][j+1]+a[j][i];
	for (int j=1;j<=n;j++)
		for (int i=1;i<j;i++)
			s2[i][j]+=s2[i-1][j];
}

LL dp()
{
	for (int i=0;i<=n;i++)
		for (int j=0;j<=n;j++)
			f[i][j]=inf;
	LL ans=s1[1][n];
	for (int i=1;i<=n;i++) ans=std::min(ans,s1[1][i]+s1[i+1][n]);
	for (int i=1;i<n;i++)
		for (int j=i+1;j<=n;j++)
			f[i][j]=s1[1][i]+s1[i+1][j]+s1[j+1][n]+s2[i][j+1];
	for (int j=1;j<=n;j++)
		for (int i=1;i<j;i++)
		{
			ans=std::min(ans,f[i][j]);
			for (int k=j+1;k<=n;k++)
				f[j][k]=std::min(f[j][k],f[i][j]+s1[j+1][k]+s2[j][k+1]-s2[i][k+1]);
		}
	return ans;
}

