		swap(now, nex);
	}
	ll res = linf;
	rep(j, 0, N) {
		rep(k, 0, N) {
			// debug(N, j, k, dp[N][j][k]);
			MIN(res, dp[now][j][k]);
		}
	}
	cout << res << "\n";
}

