constexpr int NMAX = 500;
ll fass[NMAX][NMAX];
ll bass[NMAX][NMAX];
ll fsss[NMAX][NMAX];
ll bsss[NMAX][NMAX];
ll dp[NMAX + 1][NMAX + 1];
int main()
{
    stopwatch sw;
    auto N    = in<int>();
    ll btotal = 0;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            if (i == j) { continue; }
            if (i < j) {
                fass[i][j] = in<ll>();
            } else {
                bass[i][j] = -in<ll>();
                btotal -= bass[i][j];
            }
        }
    }
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) { fsss[i][j] = fass[i][j], bsss[i][j] = bass[i][j]; }
    }
    for (int i = 0; i < N; i++) {
        for (int j = 1; j < N; j++) { fsss[i][j] += fsss[i][j - 1], bsss[i][j] += bsss[i][j - 1]; }
    }
    for (int i = 1; i < N; i++) {
        for (int j = 0; j < N; j++) { fsss[i][j] += fsss[i - 1][j], bsss[i][j] += bsss[i - 1][j]; }
    }
    auto sum  = [&](const auto sss, const int il, const int iu, const int jl, const int ju) { return sss[iu - 1][ju - 1] - (il == 0 ? 0LL : sss[il - 1][ju - 1]) - (jl == 0 ? 0LL : sss[iu - 1][jl - 1]) + (il == 0 or jl == 0 ? 0LL : sss[il - 1][jl - 1]); };
    auto fsum = [&](const int, const int p, const int np) { return sum(fsss, p, np, p, np); };
    auto bsum = [&](const int pp, const int p, const int np) { return sum(bsss, p, np, pp, np); };
    for (int np = 0; np <= N; np++) {
        for (int p = 0; p < np; p++) {
            ll min = inf_v<ll>;
            for (int pp = 0; pp <= p; pp++) {
                const ll fore = fsum(pp, p, np);
                const ll back = bsum(pp, p, np);
                chmin(min, fore + back + dp[pp][p]);
            }
            dp[p][np] = min;
        }
    }
    ll ans = inf_v<ll>;
    for (int p = 0; p <= N; p++) { chmin(ans, dp[p][N]); }
    outln(ans + btotal);
    return 0;
}

