	char ch = getchar();
	while( !isdigit( ch ) ) { if(ch == '-') f = -1; ch = getchar(); }
	while( isdigit( ch ) ) num = (num << 3) + (num << 1) + (ch ^ 48), ch = getchar();
	return num * f;
}

inline LL query(int xu, int yu, int xd, int yd, LL sum[maxN + 1][maxN + 1])
{
	return sum[xd][yd] - sum[xu - 1][yd] - sum[xd][yu - 1] + sum[xu - 1][yu - 1];
}

