namespace TYC
{
	typedef long long ll;

	const int N = 505;
	const ll INF = 0x3f3f3f3f3f3f3f3fLL;

	ll A[N][N], S1[N][N], S2[N][N], F[N][N];

	inline int read()
	{
		int x = 0, f = 0, ch = getchar();
		while (!isdigit(ch))
			f |= (ch == '-'), ch = getchar();
		while (isdigit(ch))
			x = x * 10 + ch - '0', ch = getchar();
		return f ? -x : x;
	}

	inline ll Q(const int x1, const int x2, const int y1, const int y2)
	{
		if (x1 > x2 || y1 > y2)
			return 0;
		else
			return S2[x2][y2] - S2[x1 - 1][y2] - S2[x2][y1 - 1] + S2[x1 - 1][y1 - 1];
	}

	void work()
	{
		int n = read();
		for (int i = 1; i <= n; i++)
			for (int j = 1, v; j <= n; j++)
			{
				v = i == j ? 0 : read();
				A[i][j] = A[i][j - 1] + v;
				S2[i][j] = S2[i - 1][j] + S2[i][j - 1] + v - S2[i - 1][j - 1];
			}
		for (int a = 1; a <= n; a++)
			for (int b = a; b <= n; b++)
				for (int i = a; i <= b; i++)
					S1[a][b] += A[i][b] - A[i][i];
		memset(F, 0x3f, sizeof(F));
		for (int i = 1; i <= n; i++)
			F[i][0] = S1[1][i];
		for (int i = 1; i < n; i++)
			for (int j = 0; j < i; j++)
				if (F[i][j] != INF)
					for (int k = i + 1; k <= n; k++)
						F[k][i] = min(F[k][i], F[i][j] + S1[i + 1][k] + Q(k + 1, n, j + 1, i));
		ll ans = INF;
		for (int i = 0; i <= n; i++)
			ans = min(ans, F[n][i]);
		printf("%lld\n", ans);
	}
}

int main()
{
	TYC::work();
	return 0;
}

