        for(int j = 1 ; j <= n ; ++j) {
            A[i][j] += A[i - 1][j] + A[i][j - 1] - A[i - 1][j - 1];
            B[i][j] += B[i - 1][j] + B[i][j - 1] - B[i - 1][j - 1];
        }

    for(int i = 1 ; i < n ; ++i) {
        dp[i][0] = A[i][i];
        for(int j = 1 ; j < i ; ++j) {
            dp[i][j] = inf;
            for(int k = 0 ; k < j ; ++k)
                dp[i][j] = min(dp[i][j] , dp[j][k] + S(A , j + 1 , i , i) + S(B , j + 1 , i , k));
