		ll s = 0;
		for (int j = n - 1; j >= i; j--) {
			sum2[i][j] = s;
			s += a[j][i];
		}
	}

	rep(i, n + 1)rep(j, n + 1)dp[i][j] = INF;
	dp[0][0] = 0;
	{
		ll s = 0;
		for (int j = n-1; j>0; j--) {
			for (int k = 0; k <= j; k++) {
				dp[j][k] = s + sum[0][k] + sum[k + 1][j];
			}

			s += a[j][0];
		}
	}
	for (int i = 1; i < n-1; i++) {
		rep(j, n + 1)rep(k, n + 1)cop[j][k] = INF;

		for (int k = i; k < n; k++) {
			ll mi = INF;
			for (int l = k; l <n; l++) {
				mi = min(mi, dp[l][k] - sum[k + 1][l]);
				cop[l][k] = sum2[i][l] + sum[k + 1][l] + mi;
			}
		}
		ll mi = INF;
		for (int m = i; m < n; m++) {
			mi = min(mi, dp[m][i - 1] - sum[i][m]);
			for (int l = m; l < n; l++) {
				cop[l][m] = min(cop[l][m], sum2[i][l] + sum[i][m] + sum[m + 1][l] + mi);
			}
		}
		rep(j, n + 1)rep(k, n + 1)dp[j][k]=cop[j][k];

	}
	ll ans = INF;
	rep(i, n+1)rep(j, n+1)ans = min(ans, dp[i][j]);
	cout << ans << endl;
}

