            if (used[j][i]) continue;
            reg ll sum = 0;
            reg bool check = false;
            for (reg int k = i + 1; k < j; k++) {
                if (used[i][k] || used[k][j]) { check = true; break; }
                sum += min(a[i][k], a[k][j]);
            }
