ll solve() {
	REP(i,n) FOR(j,i,n) { sumforwardcost[i][j]=0; FOR(k,i,j) sumforwardcost[i][j]+=cost[k][j]; if(i<j) sumforwardcost[i][j]+=sumforwardcost[i][j-1]; }
	REP(i,n) FOR(j,i,n) { sumbackwardcost[i][j]=0; FOR(k,i,j) sumbackwardcost[i][j]+=cost[j][k]; if(i<j) sumbackwardcost[i][j]+=sumbackwardcost[i][j-1]; }

	REP(i,n) REP(j,n) dp[i][j]=LLONG_MAX;
	REP(j,n) dp[0][j]=sumforwardcost[0][j];
	REP(i,n) FOR(j,i,n) if(dp[i][j]!=LLONG_MAX) {
