  rep(i,N){
    rep(j,N){
      if(i==j)continue;
      cin>>A[i][j];
      tot+=A[i][j];
    }
  }

  rep(i,N){
    repl(j,i+1,N){
      lpsum[i][j]=lpsum[i][j-1]+A[j][i];
    }
    for(ll j=i-1;j>=0;j--){
      psum[j][i]=psum[j+1][i]+A[i][j];
    }
    for(ll j=i-1;j>=0;j--){
      nsum[j][i]=nsum[j+1][i]+A[j][i];
    }
  }

  ll res=0;
  rep(i,N)rep(j,N)dp[i][j]=-INF;
  repl(i,1,N){
    dp[i][0]=psum[0][i]+nsum[0][i];
    for(ll j=i-1;j>=0;j--){
      dp[i][0]+=psum[0][j];
    }

    ll crt=dp[i][0];
    repl(k,i+1,N){
      crt+=nsum[0][k]-nsum[i][k];
      crt+=psum[0][k];
    }
    maxch(res,crt);
  }

  repl(i,1,N)repl(j,1,i){
    ll vp=psum[j][i];
    for(ll k=i-1;k>=j;k--){
      vp+=lpsum[k][i-1];
    }
    ll vn=nsum[0][i];
    for(ll k=i-1;k>j;k--){
      vn+=nsum[0][k]-nsum[j][k];
    }
    for(ll k=j-1;k>=0;k--){
      vp+=lpsum[k][i-1]-lpsum[k][j];
      maxch(dp[i][j],dp[j][k]+vp+vn);
    }

    ll crt=dp[i][j];
    repl(k,i+1,N){
      crt+=nsum[0][k]-nsum[i][k];
      crt+=psum[j][k];
    }
    maxch(res,crt);
  }

  cout<<tot-res<<endl;

  return 0;
}

