const int MN = 510;
int n;
ll cost[MN][MN];
ll rcost[MN][MN];
ll cost_sm[MN][MN];
ll rcost_sm[MN][MN];

ll memo[MN][MN][MN];
bool vis[MN][MN][MN];
ll calc(int a, int b, int c) {
    /*
    [0, a) : 2
    [a, b) : 1
    [b, c) : 0
     */
    if (c == n) return 0;
    if (vis[a][b][c]) return memo[a][b][c];
    vis[a][b][c] = true;    
    ll ans = TEN(18);
    /*
      c = 0
      prohibit
      2 <- 0
      0 -> 0
     */
    ans = min(ans, cost_sm[c][a] + (rcost_sm[c][c] - rcost_sm[c][b]) + calc(a, b, c + 1));
    /*
      c = -1
      prohibit
      1 <- -1      
     */
    ans = min(ans, (cost_sm[c][b]) + calc(b, c, c + 1));

    return memo[a][b][c] = ans;
}


