const int N=510;
ll a[N][N],b[N][N];
ll f[N][N];
int n;
void getsum(ll a[][N]) {
	for(int i=1;i<=n;++i)
		for(int j=1;j<=n;++j)
			a[i][j]+=a[i-1][j]+a[i][j-1]-a[i-1][j-1];
}
ll query(ll a[][N],int x1,int x2,int y1,int y2) {
	if(x2<x1||y2<y1) return 0;
	x1--,y1--;
	return a[x2][y2]-a[x1][y2]-a[x2][y1]+a[x1][y1];
}
int main() {
	rd(n);
	for(int i=1;i<=n;++i)
		for(int j=1;j<=n;++j) {
			if(i==j) continue;
			int x; rd(x);
			if(i<j) a[i][j]=x;
			if(i>j) b[i][j]=x;
		}
	getsum(a),getsum(b);
	memset(f,0x3f,sizeof(f));
	for(int i=1;i<=n;++i)
		f[0][i]=query(a,1,i-1,2,i);
	for(int i=0;i<=n-1;++i)
		for(int j=i+1;j<=n-1;++j)
			for(int k=j+1;k<=n-1;++k)
				f[j][k]=min(f[j][k],f[i][j]+query(a,j+1,k-1,j+2,k)+query(b,j+1,k,1,i));
	ll ans=query(a,1,n-1,2,n);
	for(int i=0;i<=n-1;++i)
		for(int j=i+1;j<=n-1;++j) 
			ans=min(ans,f[i][j]+query(a,j+1,n-1,j+2,n)+query(b,j+1,n,1,i));
	printf("%lld",ans);
	return 0;
}

