typedef long long LL;
const LL Infll = 0x3f3f3f3f3f3f3f3f;
const int MN = 505;

int N;
LL A[MN][MN], B[MN][MN], f[MN][MN], Ans;

inline LL val(int k, int j, int i) {
	return B[j + 1][i] + A[N][j] - A[N][k] - A[i][j] + A[i][k];
}

int main() {
	scanf("%d", &N), Ans = Infll;
	for (int i = 1; i <= N; ++i)
		for (int j = 1; j <= N; ++j)
			if (j != i) scanf("%lld", &A[i][j]);
	for (int j = 1; j <= N; ++j)
		for (int i = j; i >= 1; --i)
			B[i][j] = B[i + 1][j] + B[i][j - 1] - B[i + 1][j - 1] + A[i][j];
	for (int i = 1; i <= N; ++i)
		for (int j = 1; j <= N; ++j)
			A[i][j] += A[i - 1][j] + A[i][j - 1] - A[i - 1][j - 1];
	for (int i = 1; i <= N - 1; ++i) {
		f[i][0] = val(0, 0, i);
		Ans = std::min(Ans, f[i][0] + val(0, i, N));
		for (int j = 1; j < i; ++j) {
			f[i][j] = Infll;
			for (int k = 0; k < j; ++k)
				f[i][j] = std::min(f[i][j], f[j][k] + val(k, j, i));
			Ans = std::min(Ans, f[i][j] + val(j, i, N));
		}
	}
	printf("%lld\n", Ans);
	return 0;
}

