ll sum(int x1, int x2, int y1, int y2) {
    assert(x1 <= x2 && y1 <= y2);
    return p[x2][y2] - (x1 ? p[x1 - 1][y2] : 0) - (y1 ? p[x2][y1 - 1] : 0) +
        ((x1&&y1) ? p[x1 - 1][y1 - 1] : 0);
}

ll sum2(int x1, int x2, int y1, int y2) {
    assert(x1 <= x2 && y1 <= y2);
    return p2[x2][y2] - (x1 ? p2[x1 - 1][y2] : 0) - (y1 ? p2[x2][y1 - 1] : 0) +
        ((x1&&y1) ? p2[x1 - 1][y1 - 1] : 0);
}

