int main() {
    int n; cin >> n;
    vector<vector<int>> a(n, vector<int>(n));
    vector<vector<int64_t>> row(n, vector<int64_t>(n));
    vector<vector<int64_t>> col(n, vector<int64_t>(n));
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            if (i != j) cin >> a[i][j];
        }
    }
    for (int i = 0; i < n; ++i) {
        for (int j = n - 1; j >= 0; --j) {
            row[i][j] = a[i][j];
            if (j + 1 < n) row[i][j] += row[i][j + 1];
        }
    }
    for (int i = 0; i < n; ++i) {
        for (int j = n - 1; j >= 0; --j) {
            col[i][j] = a[j][i];
            if (j + 1 < n) col[i][j] += col[i][j + 1];
        }
    }
    constexpr int64_t kInf = 1'000'000'000'000'000'000;
    vector<vector<int64_t>> dp(n, vector<int64_t>(n, kInf));
    dp[n - 1][n - 1] = 0;
    for (int i = n - 2; i >= 0; --i) {
        vector<vector<int64_t>> nxt(n, vector<int64_t>(n, kInf));
        for (int j = 0; j < n; ++j) {
            for (int k = j; k < n; ++k) {
                if (dp[j][k] == kInf) continue;
                int64_t cost = (row[i][i + 1] - (j + 1 < n ? row[i][j + 1] : 0)) + (k + 1 < n ? col[i][k + 1] : 0);
                nxt[j][k] = min(nxt[j][k], dp[j][k] + cost);
                cost = (j + 1 < n ? col[i][j + 1] : 0);
                nxt[i][j] = min(nxt[i][j], dp[j][k] + cost);
            }
        }
        dp = nxt;
    }
    int64_t ans = kInf;
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) ans = min(ans, dp[i][j]);
    }
    cout << ans << "\n";
}

