typedef long long ll;
const int maxn = 500 + 5;
ll f[maxn][maxn];
ll sum[2][maxn][maxn];
int n;
int a[maxn][maxn];
ll cal0(int a, int b)
{
    return sum[0][b][b] - sum[0][a][b] - sum[0][b][a] + sum[0][a][a];
}
ll cal1(int a, int b, int c)
{
    return sum[1][n][b] - sum[1][n][a] - sum[1][c][b] + sum[1][c][a];
}
void init()
{
    for(int i = 1; i <= n; ++i)
    {
        for(int j = 1; j <= n; ++j)
        {
            sum[i > j][i][j] = a[i][j];
        }
    }
    for(int k = 0; k < 2; ++k)
    {
        for(int i = 1; i <= n; ++i)
        {
            for(int j = 1; j <= n; ++j)
            {
                sum[k][i][j] += sum[k][i - 1][j] + sum[k][i][j - 1] - sum[k][i - 1][j - 1];
            }
        }
    }
}
ll solve()
{
    init();
    for(int i = 1; i <= n; ++i)
    {
        for(int j = i + 1; j <= n; ++j)
        {
            f[j][i] = cal0(0, i) + cal0(i, j) + cal1(0, i, j);
        }
    }
    for(int i = 1; i <= n; ++i)
    {
        for(int j = 1; j < i; ++j)
        {
            for(int x = i + 1; x <= n; ++x)
            {
                f[x][i] = min(f[x][i], f[i][j] + cal0(i, x) + cal1(j, i, x));
            }
        }
    }
    ll an = cal0(0, n);
    for(int i = 1; i <= n; ++i)
    {
        ll r = cal0(i, n);
        an = min(an, cal0(0, i) + r);
        for(int j = 1; j < i; ++j)
        {
            an = min(an, f[i][j] + r);
        }
    }
    return an;
}
int main()
{
    read(n);
    for(int i = 1; i <= n; ++i)
    {
        for(int j = 1; j <= n; ++j) if(i != j)
        {
            read(a[i][j]);
        }
    }
    printf("%lld\n", solve());
    return 0;
}

