int dp1(int i, int j, int kind){
    if(i > j)return 0;
    if(kind == -1)return j-i+1;
    if(DP1C[i][j][kind])return DP1[i][j][kind];
    int ans = dp1(i,j-1,kind) + dp1(j,n-1,kind-1);
    DP1[i][j][kind] = ans;
    DP1C[i][j][kind] = 1;
    return ans;
}

int enumerate(int a, int b, int c){
    return dp1(0, a-1, 1) + dp1(a, b-1, 0) + dp1(b, c-1, -1);
}

ll DP[20958500] = {0};
bool DPC[20958500] = {0};

ll dp(int a, int b, int c){
    if(a < 0)return 0;
    int h = enumerate(a,b,c);
    if(DPC[h])return DP[h];
    ll ans1 = A[a][b] + B[a][c] + dp(a-1,b,c);
    ll ans2 = big*big;
    if(b > a+1)ans2 = A[a][a+1] + B[a][b-1] + dp(a-1,a+1,b-1);
    ll ans3 = B[a][a] + dp(a-1, a, a);

    ll ans = ans1;
    ll pek = 0;
    if(ans2 < ans){
        ans = ans2;
        pek = 1;
    }
    if(ans3 < ans){
        ans = ans3;
        pek = 2;
    }

    DPC[h] = 1;
    DP[h] = ans;
    //cerr << a << " " << b << " " << c << "   pek: " << pek << "  ans: " << ans << "\n";
    return ans;
}

