    setIO();

    int N; re(N); vvi a(N, vi(N));
    F0R (i, N) F0R (j, N) if (i != j) re(a[i][j]);

    map<pii, ll> dp; dp[{ 0, 1 }] = 0;

    FOR (i, 1, N) {
        decltype(dp) DP;
        trav (p, dp) {
            auto [pc, cc] = p.f; ll cost = p.s;
            {
                ll stay = cost;
                FOR (v, i - cc, i) stay += a[v][i];
                F0R (v, i - cc - pc) stay += a[i][v];
                pii key{pc, cc+1};
                DP[key] = min(stay, DP.count(key) ? DP[key] : LLONG_MAX);
            }
            {
                ll go = cost;
                F0R (v, i - cc) go += a[i][v];
                pii key{cc, 1};
                DP[key] = min(go, DP.count(key) ? DP[key] : LLONG_MAX);
            }
        }

        dp = move(DP);
    }

    ll ans = LLONG_MAX;
    trav (p, dp) ckmin(ans, p.s);
    ps(ans);

    return 0;
}

