ll Getsum1(int La, int Ra, int Lb, int Rb)
{
	if (La > Ra || Lb > Rb)return 0;
	return sum1[Ra][Rb] - sum1[Ra][Lb - 1] - sum1[La - 1][Rb] + sum1[La - 1][Lb - 1];
}
ll Getsum2(int La,int Ra,int Lb,int Rb)
{
	if (La > Ra || Lb > Rb)return 0;
	return sum2[Ra][Rb] - sum2[Ra][Lb - 1] - sum2[La - 1][Rb] + sum2[La - 1][Lb - 1];
}
