		for (int j = 1; j <= n; j++)
		{
			sum1[i][j] = sum1[i - 1][j] + sum1[i][j - 1] - sum1[i - 1][j - 1] + (i > j ? A[i][j] : 0);
			sum2[i][j] = sum2[i - 1][j] + sum2[i][j - 1] - sum2[i - 1][j - 1] + (i < j ? A[i][j] : 0);
