void Get_sum(){
  for (int i=1;i<=n;++i){
	for (int j=1;j<=n;++j){
	  if (i^j) sum[i>j][i][j]=a[i][j];
	  for (int k=0;k<2;++k) sum[k][i][j]+=sum[k][i][j-1];
	}
	for (int j=1;j<=n;++j)
	  for (int k=0;k<2;++k) sum[k][i][j]+=sum[k][i-1][j];
  }
}

LL Get_sum(int id,int x0,int y0,int x1,int y1){
  return sum[id][x1][y1]-sum[id][x0-1][y1]-sum[id][x1][y0-1]+sum[id][x0-1][y0-1];
}

LL dp[N+9][N+9],ans;

