  for (int i=0;i<=n;++i)
	for (int j=0;j<=n;++j) dp[i][j]=INF;
  dp[0][0]=0;
  for (int i=0;i<=n;++i)
	for (int j=0;j<i||j<1;++j)
	  for (int k=i+1;k<=n;++k)
		dp[k][i]=min(dp[k][i],dp[i][j]+Get_sum(1,k+1,j+1,n,i)+Get_sum(0,i+1,i+1,k,k));
  ans=INF;
  for (int i=1;i<=n;++i) ans=min(ans,dp[n][i]);
}

