const int Maxn=505;
int n,a[Maxn][Maxn];
LL dp[Maxn][Maxn];
LL S1[Maxn][Maxn],S2[Maxn][Maxn];
LL Q1(int x1,int x2,int y1,int y2){
	if (x1>x2 ||y1>y2) return 0;
	return S1[x2][y2]-S1[x1-1][y2]-S1[x2][y1-1]+S1[x1-1][y1-1];
}
LL Q2(int x1,int x2,int y1,int y2){
	if (x1>x2 || y1>y2) return 0;
	return S2[x2][y2]-S2[x1-1][y2]-S2[x2][y1-1]+S2[x1-1][y1-1];
}
