	for (int i=1;i<=n;i++){
		for (int j=1;j<=n;j++){
			if (i==j) continue;
			scanf("%d",&a[i][j]);
			if (i<j){
				S1[i][j]=a[i][j];
			}
			else{
				S2[i][j]=a[i][j];
			}
		}
	}
	for (int i=1;i<=n;i++){
		for (int j=1;j<=n;j++){
			S1[i][j]+=S1[i-1][j]+S1[i][j-1]-S1[i-1][j-1]; 
			S2[i][j]+=S2[i-1][j]+S2[i][j-1]-S2[i-1][j-1]; 
		}
	}
	for (int i=0;i<Maxn;i++){
		for (int j=0;j<Maxn;j++){
			dp[i][j]=1e18;
		}
	}
