            cin >> a[i][j];
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            dp[i][j] = 1e18;
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = i; j < n; j++) {
            if (j > i) {
                in_ones[i][j] = in_ones[i][j - 1];
                in_minus[i][j] = in_minus[i][j - 1];
            }
            // delete all minus ones
            for (int k = i; k < j; k++) {
                in_minus[i][j] += a[k][j];
                in_ones[i][j] += a[j][k];
            }
        }
    }
    for (int last = 0; last < n; last++) {
        dp[0][last] = in_minus[0][last];
    }
    for (int i = 0; i < n; i++) {
        for (int last = 0; last <= i; last++) {
            for (int j = i + 1; j < n; j++) {
                // [last, i], [i + 1, j]
                ll total = in_minus[i + 1][j];
                ll minus_last = in_minus[last][j] - in_minus[last][i] - in_minus[i + 1][j];
                ll one_last = in_ones[last][j] - in_ones[last][i] - in_ones[i + 1][j];
                ll one_all = in_ones[0][j];
                one_all -= in_ones[0][i];
                one_all -= in_ones[i + 1][j];
                one_all -= one_last;
                total += one_all;
                dp[i + 1][j] = min(dp[i + 1][j], dp[last][i] + total);
            }
        }
    }
    ll best = 1e18;
    for (int i = 0; i < n; i++) best = min(best, dp[i][n - 1]);
    cout << best;
    return 0;
}

