template<typename T, typename Uint, usize n>
auto make_v(c_arr<Uint, n> szs, const T& t = T{}) { return make_v<T, Uint, n, 0>(szs, t); }
int main()
{
    const auto N = in<int>();
    auto fass    = make_v({N, N}, 0LL);
    auto bass    = make_v({N, N}, 0LL);
    ll btotal    = 0;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j < N; j++) {
            if (i == j) { continue; }
            if (i < j) {
                fass[i][j] = in<ll>();
            } else {
                bass[i][j] = -in<ll>();
                btotal -= bass[i][j];
            }
        }
    }
    auto fsss = fass, bsss = bass;
    for (int i = 0; i < N; i++) {
        for (int j = 1; j < N; j++) {
            fsss[i][j] += fsss[i][j - 1];
            bsss[i][j] += bsss[i][j - 1];
        }
    }
    for (int i = 1; i < N; i++) {
        for (int j = 0; j < N; j++) {
            fsss[i][j] += fsss[i - 1][j];
            bsss[i][j] += bsss[i - 1][j];
        }
    }
    auto sum = [&](const auto sss, const int il, const int iu, const int jl, const int ju) {
        ll ans = sss[iu - 1][ju - 1];
        if (il > 0) { ans -= sss[il - 1][ju - 1]; }
        if (jl > 0) { ans -= sss[iu - 1][jl - 1]; }
        if (il > 0 and jl > 0) { ans += sss[il - 1][jl - 1]; }
        return ans;
    };
    auto fsum = [&](const int, const int p, const int np) { return sum(fsss, p, np, p, np); };
    auto bsum = [&](const int pp, const int p, const int np) { return sum(bsss, p, np, pp, np); };
    auto memo = make_v({N + 1, N + 1}, -1LL);
    auto dp   = mfp([&](auto&& self, const int pp, const int p) -> ll {
        if (p == N) { return 0; }
        if (memo[pp][p] != -1LL) { return memo[pp][p]; }
        ll ans = inf_v<ll>;
        for (int np = p + 1; np <= N; np++) {
            const ll fore = fsum(pp, p, np);
            const ll back = bsum(pp, p, np);
            SHOW(pp, p, np, fore);
            SHOW(pp, p, np, back);
            chmin(ans, fore + back + self(self, p, np));
        }
        SHOW(pp, p, ans);
        return memo[pp][p] = ans;
    });
    SHOW(btotal);
    outln(dp(0, 0) + btotal);
    return 0;
}

