            if(i==j){continue;}
            cin>>A[i][j];
            all+=A[i][j];
        }
    }
    vector<vector<ll>> upper(n,vector<ll>(n,0));
    vector<vector<ll>> lower(n,vector<ll>(n,0));
    for(int i=0;i<n;i++){
        for(int j=0;j<n;j++){
            if(i<j){upper[i][j]=A[i][j];}
            else{lower[i][j]=A[i][j];}
        }
    }
    for(int i=1;i<n;i++){
        for(int j=0;j<n;j++){
            upper[i][j]+=upper[i-1][j];
            lower[i][j]+=lower[i-1][j];
        }
    }
    for(int i=0;i<n;i++){
        for(int j=1;j<n;j++){
            upper[i][j]+=upper[i][j-1];
            lower[i][j]+=lower[i][j-1];
        }
    }

