    auto sum=[&](const vector<vector<ll>> &Ab,ll xs,ll xl,ll ys,ll yl)->ll{
        if(xs>xl || ys>yl){return 0LL;}
        return sum2(Ab,xl,yl)-sum2(Ab,xl,ys-1)-sum2(Ab,xs-1,yl)+sum2(Ab,xs-1,ys-1);
    };

    vector<vector<ll>> dp(n,vector<ll>(n,0));
    for(ll i=0;i<n;i++){
        for(ll j=i;j<n;j++){
            for(ll m=i;m<=j;m++){
                dp[i][j]=max(dp[i][j],sum(upper,i,m-1,m,j));
            }
            //cout<<i<<" "<<j<<" "<<dp[i][j]<<endl;
            dp[i][j]+=sum(lower,i,j,i,j);
            dp[i][j]=max(dp[i][j],sum(upper,i,j,i,j));
            //cout<<dp[i][j]<<endl;
        }
    }
    for(ll w=0;w<n;w++){
        for(ll lf=0;lf+w<n;lf++){
            ll i=lf,j=lf+w;
            if(i+1<n){dp[i][j]=max(dp[i][j],dp[i+1][j]);}
            if(j-1>=0){dp[i][j]=max(dp[i][j],dp[i][j-1]);}
            for(ll m=i+1;m<=j;m++){
                dp[i][j]=max(dp[i][j],dp[i][m-1]+dp[m][j]);
            }
        }
    }
    ll mx=0;
    for(auto &I:dp){for(auto &J:I){mx=max(mx,J);}}
    cout<<all-mx<<endl;



    return 0;
}

