  auto d = make_higher_vector<3, lint>(n, n + 1, n + 1);
  for (int j = 0; j < n; j++)
  {
    at(b, 0, j) = 0;
  }
  for (int i = 0; i < n; i++)
  {
    for (int j = 0; j < n; j++)
    {
      auto crr = at(b, i, j);
      at(b, i + 1, j) = crr + at(a, i, j);
    }
