      {
        // auto crr = at(d, j, k, i);
        // at(d, j, k, i + 1) = crr - at(b, j, i) + at(b, k, i);
        d[j][k][i + 1] = d[j][k][i] - b[j][i] + b[k][i];
      }
    }
  }
  constexpr long long inf = 1LL << 60;
  auto dp = make_higher_vector<2, lint>(n, n + 1, inf);
  for (int k = 1; k <= n; k++)
  {
